pragma solidity ^0.8.19;

import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "../dividendTracker/safeToken.sol";
import "../dividendTracker/dividendTracker.sol";
import "../interfaces/IUniswapV2Factory.sol";
import "../interfaces/IUniswapV2Pair.sol";
import "../interfaces/IUniswapV2Router.sol";
import "../interfaces/ISwapMitigator.sol";

contract TBLX is Context, IERC20, SafeToken {
    using Math for uint256;
    using Address for address;

    IUniswapV2Router public uniswapV2Router;
    address public uniswapV2Pair;

    // this is the address that will call reflect() via chainlink automation
    address forwarder;

    bool private inSwapAndLiquify;

    bool public swapAndLiquifyEnabled = true;

    DividendTracker public dividendTracker;
    IERC20 public USDT;
    ISwapMitigator immutable swapper;

    // 100 for testnet tests
    uint256 public swapTokensAtAmount = 10000 * (10 **8); // 1000 * (10**8);
    uint256 public USDTRewardsFee;
    uint256 public liquidityFee;
    uint256 public totalTax;

    // amount of tokens in contract belonging to fees.
    uint256 public feesOnHand;

    uint256 public maxBalance = 365_000 * (10**8);

    // use by default 300,000 gas to process auto-claiming dividends
    uint256 public gasForProcessing = 300_000;

    uint256 public constant dailyReflection = 367_780 * (10**8);
    uint256 public reflectionPool = 134_240_000 * (10**8);

    // store addresses that a automatic market maker pairs. Any transfer *to* these addresses
    // could be subject to a maximum transfer amount
    mapping (address => bool) public automatedMarketMakerPairs;

    mapping (address => uint256) private _rOwned;
    mapping (address => uint256) private _tOwned;
    mapping (address => mapping (address => uint256)) private _allowances;

    // excludes from max balance limits
    mapping (address => bool) public _excludeFromMax;

    // excludes from reflections
    mapping (address => bool) private _isExcludedFromReflection;
    address[] private _excluded;

    bool public feesEnabled;
    bool public maxBalanceEnabled;
   
    uint256 private constant MAX = ~uint256(0);
    uint256 private _tTotal = 1_781_240_000 * (10**8);
    uint256 private _rTotal = (MAX - (MAX % _tTotal)) / 2; // we halve/reduce this value to accommodate minting
    uint256 private _tFeeTotal;

    string private constant _name = 'TRIPLE BOTTOM LINE Crypto - Infinite Supply TOKEN';
    string private constant _symbol = 'TBLX1.0';
    uint8 private constant _decimals = 8;

    event ExcludeFromReflections(address account);
    event FeesEnabled(bool status);

    event UpdateUniswapV2Router(address indexed newAddress, address indexed oldAddress);
    
    event ExcludeFromFees(address indexed account, bool isExcluded);
    event ExcludeMultipleAccountsFromFees(address[] accounts, bool isExcluded);

    event SetAutomatedMarketMakerPair(address indexed pair, bool indexed value);

    event GasForProcessingUpdated(uint256 indexed newValue, uint256 indexed oldValue);

    event SwapAndLiquifyEnabledUpdated(bool enabled);

    event SwapAndLiquify(
        uint256 tokensIntoLiqudity,
        uint256 ethReceived
    );

    event SendDividends(
    	uint256 tokensSwapped,
    	uint256 amount
    );

    event ProcessedDividendTracker(
    	uint256 iterations,
    	uint256 claims,
        uint256 lastProcessedIndex,
    	bool indexed automatic,
    	uint256 gas,
    	address indexed processor
    );

    event MaxBalanceEnabled(bool enabled);
    event SetForwarder(address forwarder);

    constructor (address _USDT, address _router, uint256 _usdtFee, uint256 _liqFee, address _swapper, address _owner)  {
        USDT = IERC20(_USDT);
        swapper = ISwapMitigator(_swapper);
        USDTRewardsFee = _usdtFee;
        liquidityFee = _liqFee;
        totalTax = _usdtFee + _liqFee;

        // mint to owner wallets and split amounts
        
        (uint256 rAmountOwner) = _getValues(_tTotal - reflectionPool);
        _rOwned[_owner] = rAmountOwner;

        (uint256 rAmountThis) = _getValues(reflectionPool);
        _rOwned[address(this)] = rAmountThis;

        dividendTracker = new DividendTracker(_USDT, address(this));
        uniswapV2Router = IUniswapV2Router(_router);

        address _uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory())
            .createPair(address(this), _USDT);
        updatePool(_uniswapV2Pair);
        _setAutomatedMarketMakerPair(_uniswapV2Pair, true);
        
        uniswapV2Pair = _uniswapV2Pair;

        // exclude from reflections
        excludeAccount(_owner);
        excludeAccount(address(this));
        excludeAccount(address(uniswapV2Router));
        excludeAccount(_uniswapV2Pair);
        
        // exclude from receiving dividends
        dividendTracker.excludeFromDividends(address(dividendTracker));
        dividendTracker.excludeFromDividends(address(this));
        dividendTracker.excludeFromDividends(_owner);
        
        // exclude from max balance
        _excludeFromMax[_owner] = true;
        _excludeFromMax[address(this)] = true;
        _excludeFromMax[_uniswapV2Pair] = true;
       
        emit Transfer(address(0), _owner, _tTotal - reflectionPool);
        emit Transfer(address(0), address(this), reflectionPool);
    }

    /**
    * @dev external function - excludes pool address from dividends and registers as an address that will trigger fees if it is sender/receive
    */
    function setAutomatedMarketMakerPair(address pair, bool value) public onlyOwner {
        require(pair != uniswapV2Pair, "invalid");
        updatePool(pair);
        _setAutomatedMarketMakerPair(pair, value);
    }

    /**
    * @dev internal function - excludes pool address from dividends and registers as an address that will trigger fees if it is sender/receive
    */
    function _setAutomatedMarketMakerPair(address pair, bool value) internal {
        require(automatedMarketMakerPairs[pair] != value, "invalid");
        automatedMarketMakerPairs[pair] = value;

        if(value) {
            dividendTracker.excludeFromDividends(pair);
        }

        emit SetAutomatedMarketMakerPair(pair, value);
    }

    /**
    * @dev change the amount of gas to spend on auto-claims at the end of each transfer
    */
    function updateGasForProcessing(uint256 newValue) public onlyOwner {
        require(newValue >= 200000 && newValue <= 500000, "must be between 200,000 and 500,000");
        require(newValue != gasForProcessing, "invalid");
        emit GasForProcessingUpdated(newValue, gasForProcessing);
        gasForProcessing = newValue;
    }

    /**
    * @dev changes the cooldown time between dividend claims
    */
    function updateClaimWait(uint256 claimWait) external onlyOwner {
        dividendTracker.updateClaimWait(claimWait);
    }

    /**
    * @dev returns the cooldown time between dividend claims (per account)
    */
    function getClaimWait() external view returns(uint256) {
        return dividendTracker.claimWait();
    }

    /**
    * @dev returns total amount of USDT dividends distributed by dividend tracker
    */
    function getTotalDividendsDistributed() external view returns (uint256) {
        return dividendTracker.totalDividendsDistributed();
    }

    /**
    * @dev returns amount of USDT able to claim by account
    */
    function withdrawableDividendOf(address account) public view returns(uint256) {
    	return dividendTracker.withdrawableDividendOf(account);
  	}

    /**
    * @dev dividend token balance of account. Should be equal to TBLX balance, or zero if exempt from dividends.
    */
	function dividendTokenBalanceOf(address account) public view returns (uint256) {
		return dividendTracker.balanceOf(account);
	}

    /**
    * @dev returns dividendend holder info of a given account
    */
    function getAccountDividendsInfo(address _account)
        external view returns (
            address account,
            int256 index,
            int256 iterationsUntilProcessed,
            uint256 withdrawableDividends,
            uint256 totalDividends,
            uint256 lastClaimTime,
            uint256 nextClaimTime,
            uint256 secondsUntilAutoClaimAvailable) {
        return dividendTracker.getAccount(_account);
    }

    /**
    * @dev returns dividend holder info of holder at given index location (array of users in dividend tracker)
    */
	function getAccountDividendsInfoAtIndex(uint256 _index)
        external view returns (
            address account,
            int256 index,
            int256 iterationsUntilProcessed,
            uint256 withdrawableDividends,
            uint256 totalDividends,
            uint256 lastClaimTime,
            uint256 nextClaimTime,
            uint256 secondsUntilAutoClaimAvailable) {
    	return dividendTracker.getAccountAtIndex(_index);
    }

    /**
    * @dev manual function to process claims (without using transfers)
    */
	function processDividendTracker(uint256 gas) external {
		(uint256 iterations, uint256 claims, uint256 lastProcessedIndex) = dividendTracker.process(gas);
		emit ProcessedDividendTracker(iterations, claims, lastProcessedIndex, false, gas, tx.origin);
    }
    
    /**
    * @dev manual function to claim dividends
    */
    function claim() external {
		dividendTracker.processAccount(msg.sender, false);
    }

    /**
    * @dev returns the index of the last user to have been auto-claimed
    */
    function getLastProcessedIndex() external view returns(uint256) {
    	return dividendTracker.getLastProcessedIndex();
    }

    /**
    * @dev returns amount of users currently receiving dividends
    */
    function getNumberOfDividendTokenHolders() external view returns(uint256) {
        return dividendTracker.getNumberOfTokenHolders();
    }

    /**
    * @dev excludes a wallet from dividends
    */
    function excludeFromDividends(address account) external onlyOwner {
        dividendTracker.excludeFromDividends(account);
    }

    /**
    * @dev disables all swapAndLiquify even if sufficient fees are collected
    */
    function setSwapAndLiquifyEnabled(bool _enabled) public onlyOwner {
        swapAndLiquifyEnabled = _enabled;
        emit SwapAndLiquifyEnabledUpdated(_enabled);
    }

    /**
    * @dev Mints any amount of TBLX. Can only be called by owner
    */
    function mint(uint256 amt) external onlyOwner {
        uint256 rAmt = reflectionFromToken(amt);
        
        _tTotal += amt;
        _rTotal += rAmt;
        _rOwned[msg.sender] += rAmt;
        if (_isExcludedFromReflection[msg.sender]) {
            _tOwned[msg.sender] += amt;
        }

        emit Transfer(address(0), msg.sender, amt);
    }


    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _tTotal;
    }

    function balanceOf(address account) public view override returns (uint256) {
        if (_isExcludedFromReflection[account]) return _tOwned[account];
        return tokenFromReflection(_rOwned[account]);
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    // USE FOR UNIT TEST
    function rOwned(address user) external view returns(uint256) {
        return _rOwned[user];
    }

    function rTotal() external view returns(uint256) {
        return _rTotal;
    }
    // ---------------------------------

    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, _msgSender(), _allowances[sender][_msgSender()] - amount);
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender] + addedValue);
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender] - subtractedValue);
        return true;
    }

    function isExcludedFromReflection(address account) public view returns (bool) {
        return _isExcludedFromReflection[account];
    }

    /**
    * @dev total amount of reflections awarded
    */
    function totalReflections() public view returns (uint256) {
        return _tFeeTotal;
    }

    /**
    * @dev This function reflects the daily allocation of tokens to users once a day.
    * @dev this is the only way reflections are awarded in this protocol
    * @dev Similar to an airdrop, albeit in a gas efficient way (at the cost of utilizing r/t spaces)
    */
    function reflect() public { // Do we make this onlyOwner?
        require(forwarder != address(0), "initialize forwarder");
        require(msg.sender == forwarder || msg.sender == owner(), "Unauthorized");
        uint256 bal = balanceOf(address(this)) - feesOnHand;
        require(bal >= 0, "ran out of daily supply");
        uint256 rAmount;

        // if there is sufficient reflectionBalance, drop dailyReflection
        if (bal > dailyReflection) {
            rAmount = _getValues(dailyReflection);
            reflectionPool -= dailyReflection;
        } else {
            rAmount = _getValues(bal);
            reflectionPool -= bal;
        }
        
        _rOwned[address(this)] = _rOwned[address(this)] - rAmount;
        _tOwned[address(this)] = _tOwned[address(this)] - dailyReflection;
        _rTotal = _rTotal - rAmount;
        _tFeeTotal = _tFeeTotal + dailyReflection;
        
    }

    /**
    * @dev converts a tValue into an rValue
    */
    function reflectionFromToken(uint256 tAmount) public view returns(uint256) {
        require(tAmount <= _tTotal, "must be less than supply");
        (uint256 rAmount) = _getValues(tAmount);
        return rAmount;
    }

    /**
    * @dev converts an rValue into a tValue
    */
    function tokenFromReflection(uint256 rAmount) public view returns(uint256) {
        require(rAmount <= _rTotal, "must be less than total reflections");
        uint256 currentRate =  _getRate();
        return rAmount / currentRate;
    }

    /**
    * @dev excludes account from receiving reflections
    */
    function excludeAccount(address account) public onlyOwner() {
        require(!_isExcludedFromReflection[account], "already excluded");
        if(_rOwned[account] > 0) {
            _tOwned[account] = tokenFromReflection(_rOwned[account]);
        }
        _isExcludedFromReflection[account] = true;
        _excluded.push(account);
        emit ExcludeFromReflections(account);
    }

    /**
    * @dev includes an excluded account to reflections. This contract CANNOT be included.
    */
    function includeAccount(address account) external onlyOwner() {
        require(account != address(this), "Invalid");
        require(_isExcludedFromReflection[account], "already included");
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (_excluded[i] == account) {
                _excluded[i] = _excluded[_excluded.length - 1];
                _tOwned[account] = 0;
                _isExcludedFromReflection[account] = false;
                _excluded.pop();
                break;
            }
        }
    }

    function _approve(address owner, address spender, uint256 amount) private {
        require(owner != address(0), "zero address");
        require(spender != address(0), "zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    /**
    * @dev 1. checks if contract has collected fees greater than or equal to the threshold for "swapAndLiquify". Activate "swapAndLiquify" if conditions are met
    * @dev 2. if fees are enabled AND sender or receive is a tracked liquidity pool, calculate taxes and credit the contract (exempt if swapAndLiquify in prog)
    * @dev 3. check if receiver is exempt from max balance. If not, check if new receive amount would cause user to go over.
    * @dev 4. perform standard r/t balance transfer. if sender/receiver is excluded from reflections, add/subtract tOwned as well as rOwned.
    * @dev 5. set new balances in dividend tracker
    * @dev 6. if not in swapAndLiquify, attempt to auto-claim USDT via dividendTracker. Will only consume up to 300k gas units worth of claims.
    */
    function _transfer(address sender, address recipient, uint256 amount) private {
        require(sender != address(0), "zero address");
        require(recipient != address(0), "zero address");
        require(amount > 0, "greater than zero");
        uint256 receives = amount;
        
        uint256 contractTokenBalance = reflectionPool < _tOwned[address(this)] ? _tOwned[address(this)] - reflectionPool : 0;
        
        bool overMinTokenBalance = contractTokenBalance >= swapTokensAtAmount;
        
        if(
            overMinTokenBalance &&
            !inSwapAndLiquify &&
            !automatedMarketMakerPairs[sender] && 
            swapAndLiquifyEnabled
        ) {
            swapAndLiquify(contractTokenBalance);
            feesOnHand = _tOwned[address(this)] - reflectionPool;
        }

        if (feesEnabled) {
            if ((automatedMarketMakerPairs[sender] || automatedMarketMakerPairs[recipient]) && !inSwapAndLiquify) {

                uint256 fees = (amount*totalTax)/100;
                uint256 rAmt = _getValues(fees);
                receives = amount-fees;

                _tOwned[address(this)] += fees;
                _rOwned[address(this)] += rAmt;
                feesOnHand += fees;
                emit Transfer(msg.sender, address(this), fees);
            }
        }
        
        
        if (!_excludeFromMax[recipient] && maxBalanceEnabled) {
            require(balanceOf(recipient) + receives <= maxBalance, "Cannot own above max");
        }

        (uint256 rAmount, uint256 rReceive) = _getValuesBoth(amount, receives);
        _rOwned[sender] -= rAmount;
        _rOwned[recipient] += rReceive;
        if (_isExcludedFromReflection[sender]) {
            _tOwned[sender] -= amount;
        }
        if (_isExcludedFromReflection[recipient]) {
            _tOwned[recipient] += receives;
        }
        emit Transfer(sender, recipient, receives);
        

        try dividendTracker.setBalance(sender, balanceOf(sender)) {} catch {}
        try dividendTracker.setBalance(recipient, balanceOf(recipient)) {} catch {}

        if(!inSwapAndLiquify) {
	    	uint256 gas = gasForProcessing;

	    	try dividendTracker.process(gas) returns (uint256 iterations, uint256 claims, uint256 lastProcessedIndex) {
	    		emit ProcessedDividendTracker(iterations, claims, lastProcessedIndex, true, gas, tx.origin);
	    	} 
	    	catch {

	    	}
        }
    }

    /**
    * @dev returns a single rValue from a single tValue param
    */
    function _getValues(uint256 tAmount) private view returns (uint256) {
        uint256 currentRate =  _getRate();
        (uint256 rAmount) = _getRValues(tAmount, currentRate);
        return (rAmount);
    }

    /**
    * @dev returns two rValues calculated from two tValue params
    */
    function _getValuesBoth(uint256 tAmount, uint256 tReceive) private view returns (uint256, uint256) {
        uint256 currentRate =  _getRate();
        (uint256 rAmount) = _getRValues(tAmount, currentRate);
        uint256 rReceive = _getRValues(tReceive, currentRate);
        return (rAmount, rReceive);
    }

    /**
    * @dev called by _getValues and _getValuesBoth. converts a tValue into an rValue. Requires rate
    */
    function _getRValues(uint256 tAmount, uint256 currentRate) private pure returns (uint256) {
        uint256 rAmount = tAmount * currentRate;
        return (rAmount);
    }

    /**
    * @dev calculates ratio by first acquiring accurate rSupply and tSupply from _getCurrentSupply
    */
    function _getRate() private view returns(uint256) {
        (uint256 rSupply, uint256 tSupply) = _getCurrentSupply();
        return rSupply / tSupply;
    }

    /**
    * @dev returns converted rSupply and tSupply (for calculating ratio) by deducting exempted amounts
    */
    function _getCurrentSupply() private view returns(uint256, uint256) {
        uint256 rSupply = _rTotal;
        uint256 tSupply = _tTotal;      
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (_rOwned[_excluded[i]] > rSupply || _tOwned[_excluded[i]] > tSupply) return (_rTotal, _tTotal);
            rSupply = rSupply - _rOwned[_excluded[i]];
            tSupply = tSupply - _tOwned[_excluded[i]];
        }
        if (rSupply < _rTotal / _tTotal) return (_rTotal, _tTotal);
        return (rSupply, tSupply);
    }

    
    /**
    * @dev Should swap 11/12 of fees collected into USDT, saving 1/12 of fees for adding liquidity.
    * @dev of the USDT swapped, a portion goes to liquidity while the rest is redistributed to holders (via dividend tracker)
    */
    function swapAndLiquify(uint256 contractTokenBalance) private lockTheSwap {
        // take liquidity fee, keep a half token
        // halfLiquidityToken = totalAmount * (liquidityFee/2totalFee)

        // K
        uint256 tokensToAddLiquidityWith = ((contractTokenBalance * liquidityFee) * 2) / 100;
        // swap the remaining to BNB

        // Z
        uint256 toSwap = contractTokenBalance-tokensToAddLiquidityWith;

        // capture the contract's current USDT balance.
        // this is so that we can capture exactly the amount of ETH that the
        // swap creates, and not make the liquidity event include any ETH that
        // has been manually sent to the contract
        uint256 initialBalance = USDT.balanceOf(address(this));

        // swap tokens for USDT
        swapTokensForUSDT(toSwap, address(swapper)); // <- send to swapper because address(this) can't receive erc20-erc20 swaps (uniV2 issue)
        swapper.returnToToken(); // brings USDT back here
        uint256 deltaBalance = USDT.balanceOf(address(this))-initialBalance;

        uint256 usdtToAddLiquidityWith = deltaBalance * tokensToAddLiquidityWith / toSwap;
        
        // add liquidity to uniswap
        addLiquidity(tokensToAddLiquidityWith, usdtToAddLiquidityWith);

        uint256 dividends = USDT.balanceOf(address(this));
        if(USDT.allowance(address(this), address(dividendTracker)) < dividends) {
          USDT.approve(address(dividendTracker), ~uint256(0));
        }
        dividendTracker.payUSDT(dividends);

        
        emit SendDividends(toSwap-tokensToAddLiquidityWith, dividends);
        emit SwapAndLiquify(tokensToAddLiquidityWith, deltaBalance);
    } 

    /**
    * @dev swaps collected taxes to USDT on swapAndLiquify. Uses the default pool (created in constructor)
    */
    function swapTokensForUSDT(uint256 tokenAmount, address _to) internal {

        
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = address(USDT);

        if(allowance(address(this), address(uniswapV2Router)) < tokenAmount) {
          _approve(address(this), address(uniswapV2Router), tokenAmount);
        }

        // make the swap
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of USDT
            path,
            _to,
            block.timestamp
        );
        
    }

    /**
    * @dev adds USDT/Token liquidity on swapAndLiquify. Uses the default pool (created in constructor)
    */
    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) internal {
        if(USDT.allowance(address(this), address(uniswapV2Router)) < ethAmount) {
          USDT.approve(address(uniswapV2Router), ethAmount);
        }
        if(allowance(address(this), address(uniswapV2Router)) < tokenAmount) {
          _approve(address(this), address(uniswapV2Router), tokenAmount);
        }
        // add the liquidity
        uniswapV2Router.addLiquidity(
            address(this),
            address(USDT),
            tokenAmount,
            ethAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            address(this), // owner(),
            block.timestamp
        );
        
    }

    // Owner Functions 

    /**
    * @dev flips the feesEnabled flag. If false, all fees are omitted from trades
    */
    function flipFees() external onlyOwner {
        feesEnabled = !feesEnabled;
        emit FeesEnabled(feesEnabled);
    }

