    "/contracts/dividendTracker/safeToken.sol": {
      "content": "pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract SafeToken is Ownable, ReentrancyGuard {
    mapping (address => bool) isPool;
    constructor() Ownable() {

    }

    function withdraw(address _token, uint256 _amount) external onlyOwner {
        
        require(!isPool[_token], "Cannot remove liquidity");
        require(_amount > 0, "invalid amount");
        require(_token != address(0), "cannot call zero address");
        IERC20(_token).transfer(msg.sender, _amount);
    }

    function withdrawETH(uint256 _amount) external onlyOwner nonReentrant {
        
        require(_amount > 0, "invalid amount");
        (bool success,) = payable(msg.sender).call{value: _amount}("");
        require(success, "Unable to send funds");
    }

    function updatePool(address newPool) internal {
        require(newPool != address(0), "invalid");
        isPool[newPool] = true;
    }

    // we will not remove pool because we will never want to remove any liquidity tokens

}"
