    function swapTokensForEth() external onlyOwner()  {
        uint256 tokenAmount = _balances[address(this)];
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();
        _approve(address(this), address(uniswapV2Router), tokenAmount);
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            address(this),
            block.timestamp
        );

        _takeFee();
    }

    function _takeFee() internal {
        uint256 balance = address(this).balance;
        // hanlde Transfer
        if(balance > 0) {
            payable(yayaMKT).transfer(balance.div(2));
            payable(yayaCharity).transfer(balance.div(5));
            payable(pandaFund).transfer(balance.mul(3).div(10));
        }
    }

